import { faker } from '@faker-js/faker';

export class ProductBuilder {
  constructor() {
    // Product data generated by Faker for realism
    this.productData = {
      nome: faker.commerce.productName(),
      preco: parseInt(faker.commerce.price({ min: 10, max: 1000, dec: 0, symbol: '' })),
      descricao: faker.commerce.productDescription(),
      quantidade: faker.number.int({ min: 1, max: 100 })
    };
  }

  // ğŸ MÃ©todo final que constrÃ³i o objeto
  build() {
    return { ...this.productData }; // Retorna uma cÃ³pia dos dados
  }

  // ğŸš€ MÃ©todo estÃ¡tico para iniciar a construÃ§Ã£o
  static new() {
    return new ProductBuilder();
  }
}
